def DiffieHellman(group, peer):
    p, g, l = PRIMES[group]
    a = random.randrange(1, p)
    return pow(g, a, p).to_bytes(l, 'big'), pow(int.from_bytes(peer, 'big'), a, p).to_bytes(l, 'big')

